"""
bcfortiapi.fgt\n
    *Python library for communicating with Fortinet FortiGate*

Dependencies:
------------\n
    *requests, json, urllib3*

Examples:
---------\n
    *Import into Python script*
>>> import bcfortiapi

    *Initialise instance of fmgapi within script*
>>> init_variable = bcfortiapi.fgtapi(fortigate="FGT IP or FQDN", port="FGT HTTPS Admin Port", authtoken="FGT API Token", debug=True/False)

Notes:
------\n
    *If authtoken is supplied when initialising the fgtapi class then fgtapi.login and fgtapi.logout functions are not required*

"""

import requests
import json
from urllib3 import disable_warnings, exceptions

class fgtapi:

    """
    bcfortiapi.fgt.fgtapi\n
        *Contains functions for generating calls to the Fortinet FortiGate API*
    
    Examples:
    ---------\n
        *Login (Basic Authentication Only)*
    >>> init_variable.login(username="Username", password="Password")
    
        *Login and return login state (bool)*
    >>> response_variable = init_variable.login(username="Username", password="Password")

        *Example GET (response returned as string, can be read using json.loads)*
    >>> response_variable = init_variable.dvmdb_device(adom="ADOM name", method="get")

        *Example GET (with URL options)*
    >>> response_variable = init_variable.dvmdb_device(adom="ADOM name", method="get", urloptions="vdom=root&search=string")

        *Logout (Basic Authentication Only)*
    >>> init_variable.logout()

    """

    #----------------------------------------
    #---------- Internal Functions ----------
    #----------------------------------------

    def __init__(self, fortigate:str="127.0.0.1", port:str="443", authtoken:str=None, version:str="7.4", debug:bool=False):
        self.session = requests.Session()
        self.session.verify = False
        self.base_url = f"https://{fortigate}:{port}/"
        self.db_ver = version
        if (len(version) == 3) and (version[1]) == ".":
            splitversion = version.split(".")
            self.db_maj_ver = int(splitversion[0])
            self.db_min_ver = int(splitversion[1])
        else:
            self.db_maj_ver = 7
            self.db_min_ver = 4
        if authtoken is not None:
            self.loginstate = True
        else:
            self.loginstate = False
        self.payload = None
        self.debug = debug
        self.authtoken = authtoken
        disable_warnings(exceptions.InsecureRequestWarning)
        if self.debug == True:
            self._debugger(fnct=self.__init__.__name__, mode=["std"])

    def _debugger(self, fnct=None, resp=None, mode:list=["std"]):
        if fnct is not None:
            print(f"*** bcfortiapi.fgt.fgtapi.{str(fnct)} Debug Output ***")
            print("---------------------------------------------------------------------------------")
        else:
            print(f"*** bcfortiapi.fgt.fgtapi Debug Output ***")
            print("---------------------------------------------------------------------------------")
        for i in range(len(mode)):
            if str(mode[i]) == "std":
                print(f"Session Verification: {str(self.session.verify)}")
                print(f"FGT Base URL: {str(self.base_url)}")
                print(f"Specified FGT OS Version: {str(self.db_ver)}")
                print(f"Calculated Major Version Integer Value: {str(self.db_maj_ver)}")
                print(f"Calculated Minor Version Integer Value: {str(self.db_min_ver)}")
                print(f"Login State: {str(self.loginstate)}")
                print(f"Payload: {str(self.payload)}")
                print("")
            if (str(mode[i]) == "resp") and (resp is not None):
                print("JSON Response")
                print("")
                print(str(resp))
                print("")

    def _json_error(self, fnct=None, msg:str=None):
        err = {
            "type": "error",
            "detail": "internal",
            "function": fnct,
            "message": msg
        }
        err = json.dumps(err)
        err_json = json.loads(err)
        return err_json
    
    def _request(self, method:str="get", endpoint:str=None, urloptions:str=None):
        if urloptions is not None:
            apiurl = self.base_url + endpoint + "?" + urloptions
        else:
            apiurl = self.base_url + endpoint
        if self.authtoken is not None:
            authheader = {
                "Authorization": "Bearer " + self.authtoken
            }
            if method == "post":
                response = self.session.post(url=apiurl, headers=authheader, data=self.payload)
            elif method == "put":
                response = self.session.put(url=apiurl, headers=authheader, data=self.payload)
            elif method == "delete":
                response = self.session.delete(url=apiurl, headers=authheader)
            else:
                response = self.session.get(url=apiurl, headers=authheader)
        else:
            if method == "post":
                response = self.session.post(url=apiurl, data=self.payload)
            elif method == "put":
                response = self.session.put(url=apiurl, data=self.payload)
            elif method == "delete":
                response = self.session.delete(url=apiurl)
            else:
                response = self.session.get(url=apiurl)
        resp_req = response.content
        response = resp_req
        if self.debug == True:
            self._debugger(fnct=self._request.__name__, mode=["std", "resp"])
        return response
    
    #----------------------------------------------
    #---------- Authentication Functions ----------
    #----------------------------------------------

    def login(self, username:str="", password:str=""):
        """
        bcfortiapi.fgt.fgtapi.login\n
        
        API Endpoints:
        --------------\n
            */logincheck*
        
        Mandatory Parameters:
        ---------------------\n
            *username, password*
        
        Examples:
        ---------\n
            *Login*
        >>> init_variable.login(username="Username", password="Password")
        
            *Login and return login state (bool)*
        >>> response_variable = init_variable.login(username="Username", password="Password")

        """
        self.payload = {
            "username": username,
            "secretkey": password
        }
        response = self.session.post(url=self.base_url + "logincheck", data=self.payload)
        if response.ok:
            self.loginstate = True
        else:
            self.loginstate = False
        if self.debug == True:
            self._debugger(fnct=self.login.__name__, resp=response, mode=["std", "resp"])
        return self.loginstate
    
    def logout(self):
        """
        bcfortiapi.fgt.fgtapi.logout\n
        
        API Endpoints:
        --------------\n
            */logout*
        
        Mandatory Parameters:
        ---------------------\n
            *None*
        
        Examples:
        ---------\n
        >>> init_variable.logout()

        """
        if self.loginstate == True:
            response = self.session.post(url=self.base_url + "logout", data=None)
            if response.ok:
                self.loginstate = False
            if self.debug == True:
                self._debugger(fnct=self.logout.__name__, resp=response, mode=["std"])
        else:
            response = self._json_error(fnct=self.logout.__name__,msg=f"Login state is {self.loginstate}")
        return self.loginstate
    
